<script setup>
const { $anime } = useNuxtApp()
const loading = useLoading()
onMounted(() => {
  const large = $anime({
    targets: 'svg .large',
    d: [
      { value: 'M 13.939 20.524 L 7.972 17.54 M 13.939 20.524 L 19.888 17.54 M 7.939 17.524 L 5.939 16.524 L 5.939 14.024 M 5.939 14.019 L 5.939 6.524 M 5.939 6.524 L 11.923 3.534 M 11.939 3.524 L 13.939 2.524 L 15.939 3.524 M 15.955 3.531 L 21.939 6.524 M 21.939 6.524 L 21.939 13.99 M 19.939 17.524 L 21.939 16.524 L 21.939 14.024' }
    ],
    easing: 'easeInOutSine',
    duration: 1000,
    direction: 'alternate',
    loop: true
  })
  const small = $anime({
    targets: 'svg .small',
    d: [
      { value: 'M 13.939 20.524 L 13.939 18.024 M 5.939 6.524 L 7.939 7.524 M 21.939 6.524 L 19.939 7.524 M 13.939 10.524 L 7.967 7.538 M 13.939 10.524 L 19.954 7.516 M 13.939 10.524 L 13.939 17.989' }
    ],
    easing: 'easeInOutSine',
    duration: 1000,
    direction: 'alternate',
    loop: true
  })
  if (!loading.value) {
    large.pause()
    small.pause()
  }
  watch(loading, (n, o) => {
    if (n) {
      large.play()
      small.play()
    } else {
      large.pause()
      large.reset()
      small.pause()
      small.reset()
    }
  })
})

const config = useRuntimeConfig()

const api = computed(() => {
  const apiUrl = config.public.apiBaseUrl
  if (apiUrl.startsWith('https://api.bedrinth.com')) {
    return 'prod'
  } else if (apiUrl.startsWith('https://staging-api.bedrinth.com')) {
    return 'staging'
  } else if (apiUrl.startsWith('localhost') || apiUrl.startsWith('127.0.0.1')) {
    return 'localhost'
  }
  return 'foreign'
})
